(()=>{"use strict";const e=document.getElementById("location");document.querySelector(".search_btn").addEventListener("click",(()=>{fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e.value}&APPID=e87b1d0df402ffef61de75ccb3df3f8a&units=metric`,{mode:"cors"}).then((function(e){return e.json()})).then((function(e){(e=>{const t=e=>{let t=new Date(1e3*e);return t.getHours()+":"+("0"+t.getMinutes()).substr(-2)},n=document.querySelector(".city_name"),o=document.querySelector(".state_name"),m=document.querySelector(".temperature"),d=document.querySelector(".main_desc"),c=document.querySelector(".detail_desc"),r=document.getElementById("feels"),u=document.getElementById("humid"),s=document.getElementById("press"),a=document.getElementById("wind"),i=document.getElementById("sunrise"),l=document.getElementById("sunset"),h=document.getElementById("maxtemp"),f=document.getElementById("mintemp");n.textContent=`${e.name}`,o.textContent=`${e.sys.country}`,m.textContent=`${Math.round(e.main.temp)}째`,d.textContent=`${e.weather[0].main}`,c.textContent=`${e.weather[0].description}`,r.textContent=`${Math.round(e.main.feels_like)}째`,u.textContent=`${e.main.humidity}%`,s.textContent=`${e.main.pressure} hPa`,a.textContent=`${Math.round(e.wind.speed)} km/h`,i.textContent=`${t(e.sys.sunrise)} h`,l.textContent=`${t(e.sys.sunset)} h`,h.textContent=`${Math.round(e.main.temp_max)}째`,f.textContent=`${Math.round(e.main.temp_min)}째`,fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${n.textContent}&appid=e87b1d0df402ffef61de75ccb3df3f8a&units=metric`,{mode:"cors"}).then((function(e){return e.json()})).then((function(e){console.log(e)}))})(e),console.log(e)}))}))})();